import { Fragment, useState } from 'react'
import Header from './Header';
import Field from './Field';
import Submit from './Submit';

const App = () => {

    // LIST OF PEOPLE
    const [people, set_people] = useState([
        { name: 'Arto Hellas' }
    ]) 

    // INPUT STATES
    const [input, set_input] = useState({
        name: '',
        number: 0
    })

    const update = (category, value) => {
        set_input({
            ...input,
            [category]: value
        })
    }

    // ADD PERSON
    const add_person = event => {
        event.preventDefault();

        // IF INPUT IS NOT EMPTY
        if (input.name.length > 0 && input.number.length > 0) {

            // PUSH NEW ENTRY
            people.push(input)

            // UPDATE BOTH STATES
            set_people(people)
            set_input({
                name: '',
                number: 0
            })
        }
    }

    return (
        <Fragment>
            <Header text={ 'Phonebook' } />
            <form onSubmit={ add_person }>
                <Text
                    label={ 'What is the name?' }
                    value={ input.name }
                    func={
                        event => update('name', event.target.value)
                    }
                />
                <Submit label={ 'Add' } />
            </form>
            <Header text={ 'Numbers' } />
            <List data={ people } />
        </Fragment>
    )
}

const Form = ({ trigger, update, fields }) => { return (
    <form onSubmit={ trigger }>
        { fields.map(field =>
            <Field
                label={ field.label }
                value={ field.value }
                func={
                    event => update('name', event.target.value)
                }
            />
        )}
        <Text
            label={ 'What is the name?' }
            value={ input.name }
            func={
                event => update('name', event.target.value)
            }
        />
        <Submit label={ 'Add' } />
    </form>
)}

const List = ({ data }) => { return (
    data.map(person =>
        <li key={ person.name }>{ person.name }</li>
    )
)}

export default App
