import { Fragment, useState } from 'react'
import Header from './Header';
import Field from './Field';
import Submit from './Submit';

const App = () => {

    // LIST OF PEOPLE
    const [people, set_people] = useState([{
        name: 'Arto Hellas',
        number: '040'
    }])

    // DEFAULT INPUT STATE
    const default_input = {
        name: '',
        number: ''
    }

    // INPUT STATES
    const [input, set_input] = useState(default_input)

    // UPDATE INPUT STATES
    const update_field = (category, value) => {
        set_input({
            ...input,
            [category]: value
        })
    }

    // ADD PERSON
    const add_person = event => {
        event.preventDefault();

        // IF INPUT IS NOT EMPTY
        if (input.name.length > 0 && input.number.length > 0) {

            // VERIFY WHETHER THE NAME ALREADY EXISTS
            const exists = people.filter(person => person.name === input.name).length === 0 ? false : true;

            // IF IT DOES, ALERT & BREAK EARLY
            if (exists) {
                alert(input.name + ' already exists!')
                return
            }

            // PUSH NEW ENTRY
            people.push(input)

            // UPDATE BOTH STATES
            set_people(people)
            set_input(default_input)
        }
    }

    return (
        <Fragment>
            <Header text={ 'Phonebook' } />
            <Form
                trigger={ add_person }
                update={ update_field }
                fields={[{
                    id: 'name',
                    label: 'what is their name?',
                    value: input.name
                }, {
                    id: 'number',
                    label: 'what is their number?',
                    value: input.number
                }]}
            />
            <Header text={ 'Numbers' } />
            <List data={ people } />
        </Fragment>
    )
}

const Form = ({ trigger, update, fields }) => { return (
    <form onSubmit={ trigger }>
        { fields.map(field =>
            <Field
                key={ field.id }
                label={ field.label }
                value={ field.value }
                func={
                    event => update(field.id, event.target.value)
                }
            />
        )}
        <Submit label={ 'Add' } />
    </form>
)}

const List = ({ data }) => { return (
    data.map(person =>
        <li key={ person.name }>{ person.name }: { person.number }</li>
    )
)}

export default App
