import { useState, useEffect } from 'react';

const List = ({ data, keyword, remove }) => {

    // FILTERED RESULTS
    const [filtered, set_filtered] = useState([])

    // FILTER OUT GARBAGE WHEN DATA CHANGES -- FORCE LOWERCASE
    useEffect(() => {
        set_filtered(data.filter(
            person => person.name.toLowerCase().includes(keyword.toLowerCase())
        ))
    }, [data, keyword])

    // CONDITIONAL RENDERING
    switch (true) {

        case (keyword === ''): { return (
            <li>No keyword specified.</li>
        )}

        // NO PEOPLE FOUND
        case (filtered.length === 0): { return (
            <li>No-one matches keyword "{ keyword }"</li>
        )}

        // RENDER PEOPLE
        default: { return (
            filtered.map(person =>
                <li key={ person.name }>
                    { person.name }: { person.number }
                    <button onClick={ () => remove(person.id) }>Remove</button>
                </li>
            )
        )}
    }
}

export default List;
