import { useState, Fragment } from 'react';

const App = () => {

    // COMPONENT DATA
    const [local, set_local] = useState({
        good: 0,
        neutral: 0,
        bad: 0,
        total: 0
    })

    const [stats, set_stats] = useState({
        positive: 0,
        average: (local.good - local.bad) / local.total
    })

    // INCREMENT CATEGORY
    const increment = (category) => {
        set_local({
            ...local,
            [category]: local[category] + 1,
            total: local.total + 1
        })

        set_stats
    }

    return (
        <Fragment>
            <Header text={ 'Give Feedback' } />
            <Buttons
                keys={[
                    'good', 'neutral', 'bad'
                ]}
                increment={ increment }
            />
            <Header text={ 'Statistics' } />
            <Stats
                keys={ Object.keys(local) }
                values={ local }
            />
        </Fragment>
    )
}

const Header = ({ text }) => { return (
    <h1>{ text }</h1>
)}

const Buttons = ({ keys, increment }) => { return (
    keys.map((name, index) =>
        <Button
            label={ name }
            func={ () => increment(name) }
            key={ index }
        />
    )
)}

const Button = ({ label, func }) => { return (
    <button onClick={ func }>{ label }</button>
)}

const Stats = ({ keys, values }) => { return (
    keys.map((name, index) =>
        <div key={ index }>{ name }: { values[name] }</div>
    )
)}

export default App
