import { useState, useEffect, Fragment } from 'react'
import axios from 'axios'
import './interface/general.scss'

const App = () => {

    // PEOPLE
    const [people, set_people] = useState([])

    // ON LOAD, FETCH ALL PEOPLE
    useEffect(() => {
        axios.get('http://localhost:3001/api/persons').then(result => {
            set_people(result.data)
        })
    }, [])

    return (
        <div className={ 'container' }>
            <div>
                <Block header={ 'Filter Results' }>
                    <Field
                        label={ 'Filter people by name' }
                        value={ 'foo' }
                    />
                </Block>
                <Header text={ 'phonebook' } />
                <List data={ people } />
            </div>
            <div>
                <Block header={ 'add person' }>
                    <Field
                        label={ 'What is the persons name?' }
                        value={ 'foo' }
                    />
                    <Field
                        label={ 'What is the persons\'s number?' }
                        value={ 'foo' }
                    />
                </Block>
                <Block header={ 'Notifications' }>
                    Foo
                </Block>
            </div>
        </div>
    )
}

const Block = ({ header, children }) => { return (
    <Fragment>
        <Header text={ header } />
        <div className={ 'content' }>
            { children }
        </div>
    </Fragment>
)}

const Field = ({ label, value, func }) => { return (
    <div>
        <input
            placeholder={ label }
            onChange={ func }
        />
    </div>
)}

export default App;
