import { useState, useEffect } from 'react'
import { Form, Text, Number, Button } from './components/form'
import Phonebook from './components/phonebook'
import Notifications from './components/notifications'

import axios from 'axios'
import './interface/general.scss'

const App = () => {

    // LIST OF PEOPLE
    const [people, set_people] = useState([])

    // LIST OF NOTIFICATIONS
    const [notifications, set_notifications] = useState([])

    // INPUT STATES
    const [input, set_input] = useState({
        filter: '',
        name: '',
        number: ''
    })

    // ON LOAD, FETCH ALL PEOPLE
    useEffect(() => {
        axios.get('http://localhost:3001/api/persons').then(result => {
            set_people(result.data)
        }).catch(error => {
            console.log(error)
        })
    }, [])

    // UPDATE INPUT STATES
    const update_field = (category, value) => {
        set_input({
            ...input,
            [category]: value
        })
    }

    // ATTEMPT TO CREATE A NEW USER
    const create_user = (event) => {
        event.preventDefault();

        if (input.name.length !== '' && input.number !== '') {
            set_notifications([
                ...notifications, {
                    type: 'positive',
                    message: 'new user created!',
                    id: new Date()
                }
            ])
        } else {
            set_notifications([
                ...notifications, {
                    type: 'positive',
                    message: 'could not create user.',
                    id: new Date()
                }
            ])
        }
    }

    return (
        <div className={ 'container' }>
            <div>
                <Form header={ 'Filter Results' }>
                    <Text
                        label={ 'Filter people by name' }
                        value={ input.filter }
                        func={
                            event => update_field('filter', event.target.value)
                        }
                    />
                </Form>
                <Phonebook
                    header={ 'phonebook' }
                    data={ people }
                    keyword={ input.filter }
                />
            </div>
            <div>
                <Form header={ 'add person' } func={ create_user }>
                    <Text
                        label={ 'What is the persons name?' }
                        value={ input.name }
                        func={
                            event => update_field('name', event.target.value)
                        }
                    />
                    <Number
                        label={ 'What is the persons\'s number?' }
                        value={ input.number }
                        func={
                            event => update_field('number', event.target.value)
                        }
                    />
                    <Button label={ 'Create User' } />
                </Form>
                <Notifications
                    header={ 'notifications' }
                    data={ notifications }
                />
            </div>
        </div>
    )
}

export default App;
