import { useState, Fragment } from 'react';

const App = () => {

    // COMPONENT DATA
    const [local, set_local] = useState({
        good: 0,
        neutral: 0,
        bad: 0,
        total: 0
    })

    const [stats, set_stats] = useState({
        positive: 0,
        average: 0
    })

    // INCREMENT CATEGORY
    const update = (category) => {
        set_local({
            ...local,
            [category]: local[category] + 1,
            total: local.total + 1
        })

        set_stats({
            positive: local.good / local.total,
            average: (local.good - local.bad) / local.total
        })
    }

    return (
        <Fragment>
            <Header text={ 'Give Feedback' } />
            <Buttons
                keys={[
                    'good', 'neutral', 'bad'
                ]}
                increment={ update }
            />
            <Header text={ 'Statistics' } />
            <Statistics
                keys={ Object.keys({
                    ...local,
                    ...stats
                }) }
                values={{
                    ...local,
                    ...stats
                }}
            />
        </Fragment>
    )
}

const Header = ({ text }) => { return (
    <h1>{ text }</h1>
)}

const Buttons = ({ keys, increment }) => { return (
    keys.map((name, index) =>
        <Button
            label={ name }
            func={ () => increment(name) }
            key={ index }
        />
    )
)}

const Button = ({ label, func }) => { return (
    <button onClick={ func }>{ label }</button>
)}

const Statistics = ({ keys, values }) => {
    switch(values.total) {

        // NO FEEDBACK YET
        case 0: { return (
            <div>No feedback given.</div>
        )}

        // RENDER FEEDBACK
        default: { return (
            keys.map((name, index) =>
                <div key={ index }>{ name }: { values[name] }</div>
            )
        )}
    }
}

const Stat = ({ name, value }) => { return (
    <div>{ name }: { value }</div>
)}

export default App
