import { Fragment, useEffect, useState } from 'react'
import * as db_api from './funcs/db'

import Header from './components/header';
import { Field } from './components/input';
import List from './components/list';
import Form from './components/form';

const App = () => {

    // LIST OF PEOPLE
    const [people, set_people] = useState([])

    // LOAD PEOPLE FROM JSON DB
    useEffect(() => {
        db_api.fetch_people('http://localhost:3001/persons').then(result => {
            set_people(result)
        })
    }, [])

    // INPUT STATES
    const [input, set_input] = useState({
        name: '',
        number: '',
        filter: ''
    })

    // UPDATE INPUT STATES
    const update_field = (category, value) => {
        set_input({
            ...input,
            [category]: value
        })
    }

    // ADD PERSON
    const add_person = event => {
        event.preventDefault();

        // IF INPUT IS NOT EMPTY
        if (input.name.length > 0 && input.number.length > 0) {

            // VERIFY WHETHER THE NAME ALREADY EXISTS
            const exists = people.filter(person => person.name === input.name).length === 0 ? false : true;

            // IF IT DOES, ALERT & BREAK EARLY
            if (exists) {
                alert(`${ input.name } already exists!`)
                console.log('PERSON ALREADY EXISTS, ABORTING..')
                return
            }

            // CREATE PERSON OBJECT
            const person = {
                name: input.name,
                number: input.number
            }

            // PUSH PERSON TO THE DB
            db_api.create_person('http://localhost:3001/persons', person).then(status => {

                // IF SOMETHING WENT WRONG, ABORT
                if (status !== 201) {
                    console.log('COULD NOT CREATE USER, ABORTING..')
                    return
                }

                // OTHERWISE, PUSH NEW ENTRY -- TO AVOID RELOAD
                people.push(person)

                // UPDATE BOTH STATES
                set_people(people)
                set_input({
                    filter: input.filter,
                    name: '',
                    number: ''
                })
            })
        }
    }

    // PROCESS REMOVAL
    const remove_person = (id) => {

        // ATTEMPT TO REMOVE THE PERSON FROM THE DB
        db_api.remove_person(`http://localhost:3001/persons/${ id }`).then(status => {

            // IF SOMETHING WENT WRONG
            if (status !== 200) {
                console.log('COULD NOT REMOVE PERSON')
                return
            }

            // OTHERWISE, FILTER OUT PERSON
            const temp = people.filter(person => person.id !== id)

            // UPDATE STATE WITHOUT RELOADING
            set_people(temp)
        })
    }

    return (
        <Fragment>
            <Header text={ 'Phonebook' } />
            <Field
                label={ 'Filter by Name' }
                value={ input.filter }
                func={
                    event => update_field('filter', event.target.value)
                }
            />
            <Header text={ 'Add Person' } />
            <Form
                trigger={ add_person }
                update={ update_field }
                fields={[{
                    id: 'name',
                    label: 'what is their name?',
                    value: input.name
                }, {
                    id: 'number',
                    label: 'what is their number?',
                    value: input.number
                }]}
            />
            <Header text={ 'Numbers' } />
            <List
                data={ people }
                keyword={ input.filter }
                remove={ remove_person }
            />
        </Fragment>
    )
}

export default App;
