import { useState, useEffect } from 'react';

const List = ({ data, keyword, remove }) => {

    // FILTERED RESULTS
    const [filtered, set_filtered] = useState([])

    useEffect(() => {
        console.log('trigger')

        // FILTER OUT GARBAGE
        const temp = data.filter(
            person => person.name.toLowerCase().includes(keyword.toLowerCase())
        )

        // UPDATE STATE
        set_filtered(temp)
    }, [data, keyword])


    switch (filtered.length) {

        // NO PEOPLE FOUND
        case 0: { return (
            <li>No-one matches keyword "{ keyword }"</li>
        )}

        // RENDER PEOPLE
        default: { return (
            filtered.map(person =>
                <li key={ person.name }>
                    { person.name }: { person.number }
                    <button onClick={ () => remove(person.id) }>Remove</button>
                </li>
            )
        )}
    }
}

export default List;
