import { useState, useEffect, Fragment } from 'react'
import axios from 'axios'
import './interface/general.scss'

const App = () => {

    // PEOPLE
    const [people, set_people] = useState([])

    // ON LOAD, FETCH ALL PEOPLE
    useEffect(() => {
        axios.get('http://localhost:3001/api/persons').then(result => {
            set_people(result.data)
        })
    }, [])

    return (
        <div className={ 'container' }>
            <div>
                <Header text={ 'phonebook' } />
                <List data={ people } />
            </div>
            <div>
                <Header text={ 'add person' } />
                <div className={ 'content' }>
                    <Field
                        label={ 'What is the persons name?' }
                        value={ 'foo' }
                    />
                    <Field
                        label={ 'What is the persons\'s number?' }
                        value={ 'foo' }
                    />
                </div>
            </div>
        </div>
    )
}

const Block = ({ header, children }) => { return (
    <Fragment>
        <Header text={ header } />
        { children }
    </Fragment>
)}

const Field = ({ label, value, func }) => { return (
    <div>
        <input
            placeholder={ label }
            onChange={ func }
        />
    </div>
)}

const List = ({ data }) => { return (
    <div className={ 'list' }>
        { data.map(entry =>
            <div className={ 'item' } key={ entry._id }>
                <div>{ entry.name }</div>
                <div>{ entry.number }</div>
            </div>
        )}
    </div>
)}

const Header = ({ text }) => {
    return <div className={ 'header' }>{ text }</div>    
}

export default App;
