import { Fragment } from "react";
import Header from './header.js'

const Phonebook = ({ header, data }) => { return (
    <Fragment>
        <Header text={ header } />
        <div className={ 'list' }>
            <Content
            />
            { data.map(entry =>
                <div className={ 'item' } key={ entry._id }>
                    <div>{ entry.name }</div>
                    <div>{ entry.number }</div>
                </div>
            )}
        </div>
    </Fragment>
)}

const Content = ({ data }) => {
    switch (data.length) {

        // NO CONTENT FOUND, RENDER NOTHING
        case 0: { return (
            <div>No people found.</div>
        )}

        // OTHERWISE, RENDER NORMALLY
        default: { return (
            data.map(entry =>
                <div className={ 'item' } key={ entry._id }>
                    <div>{ entry.name }</div>
                    <div>{ entry.number }</div>
                </div>
            )
        )}
    }
}

export default Phonebook;
