import { useState, useEffect } from 'react'
import axios from 'axios'
import './interface/general.scss'

const App = () => {

    // PEOPLE
    const [people, set_people] = useState([])

    // ON LOAD, FETCH ALL PEOPLE
    useEffect(() => {
        axios.get('http://localhost:3001/api/persons').then(result => {
            set_people(result.data)
        })
    }, [])

    return (
        <div className={ 'container' }>
            <div>
                <Header text={ 'phonebook' } />
                <List data={ people } />
            </div>
            <div>
                <Header text={ 'add person' } />
                <div className={ 'content' }>
                    <Field
                        label={ 'Haha' }
                        value={ 'foo' }
                    />
                </div>
            </div>
        </div>
    )
}

const Field = ({ label, value, func }) => { return (
    <div>
        <input
            placeholder={ label }
            value={ value }
            onChange={ func }
        />
    </div>
)}

const List = ({ data }) => { return (
    <div className={ 'content' }>
        { data.map(entry =>
            <div className={ 'item' } key={ entry._id }>
                <div>{ entry.name }</div>
                <div>{ entry.number }</div>
            </div>
        )}
    </div>
)}

const Header = ({ text }) => {
    return <div className={ 'header' }>{ text }</div>    
}

export default App;
