import { useState, useEffect } from 'react'
import { Inputs, Field } from './components/inputs'
import Phonebook from './components/phonebook'
import Notifications from './components/notifications'

import axios from 'axios'
import './interface/general.scss'

const App = () => {

    // LIST OF PEOPLE
    const [people, set_people] = useState([])

    // LIST OF NOTIFICATIONS
    const [notifications, set_notifications] = useState([])

    const positive = () => {
        set_notifications([
            ...notifications, {
                type: 'good',
                message: 'foobar',
                id: new Date()
            }
        ])
    }

    // ON LOAD, FETCH ALL PEOPLE
    useEffect(() => {
        axios.get('http://localhost:3001/api/persons').then(result => {
            set_people(result.data)
            positive()
        })
    }, [])

    return (
        <div className={ 'container' }>
            <div>
                <Inputs header={ 'Filter Results' }>
                    <Field
                        label={ 'Filter people by name' }
                        value={ 'foo' }
                    />
                </Inputs>
                <Phonebook
                    header={ 'phonebook' }
                    data={ people }
                />
            </div>
            <div>
                <Inputs header={ 'add person' }>
                    <Field
                        label={ 'What is the persons name?' }
                        value={ 'foo' }
                    />
                    <Field
                        label={ 'What is the persons\'s number?' }
                        value={ 'foo' }
                    />
                </Inputs>
                <Notifications
                    header={ 'notifications' }
                    data={ notifications }
                />
            </div>
        </div>
    )
}

export default App;
