import {Â Fragment } from 'react';

const App = () => {
    const course = [{
        name: 'Half Stack application development',
        id: 1,
        parts: [{
            name: 'Fundamentals of React',
            exercises: 10,
            id: 1
        }, {
            name: 'Using props to pass data',
            exercises: 7,
            id: 2
        }, {
            name: 'State of a component',
            exercises: 14,
            id: 3
        }, {
            name: 'Redux',
            exercises: 11,
            id: 4
        }]
        }, 
        {
            name: 'Node.js',
            id: 2,
            parts: [
        {
            name: 'Routing',
            exercises: 3,
            id: 1
        },
        {
            name: 'Middlewares',
            exercises: 7,
            id: 2
        }
        ]
    }]

    return <Course data={ course } />
}

const Course = ({ data }) => { return (
    <Fragment>
        <Header text={ data.name + ' (' + data.id + ')' } />
        <Content data={ data.parts } />
    </Fragment>
)}

const Header = ({ text }) => { return (
    <h1>{ text }</h1>
)}

const Content = ({ data }) => {

    // COMPUTE CUMULATIVE EXERCISES
    const n_exercises = data.reduce((partial, a) => partial + a.exercises, 0)

    return (
        <Fragment>
            { data.map(row => 
                <Part
                    key={ row.id }
                    details={ row }
                />
            )}
            <br />
            <div><b>Total of { n_exercises } exercises.</b></div>
        </Fragment>
    )
}

const Part = ({ details }) => { return (
    <div>{ details.name } { details.exercises }</div>
)}

export default App;
