import { Fragment, useState } from 'react'
import Header from './Header';
import Field from './Field';
import Submit from './Header';

const App = () => {

    // LIST OF PEOPLE
    const [people, set_people] = useState([
        { name: 'Arto Hellas' }
    ]) 

    // INPUT STATE
    const [input, set_input] = useState('')

    // ADD PERSON
    const add_person = event => {
        event.preventDefault();

        // IF INPUT IS NOT EMPTY
        if (input.length > 0) {

            // PUSH NEW PERSON
            people.push({
                name: input
            })

            // UPDATE BOTH STATES
            set_people(people)
            set_input('')
        }
    }

    return (
        <Fragment>
            <Header text={ 'Phonebook' } />
            <form onSubmit={ add_person }>
                <Field
                    label={ 'Foo' }
                    value={ input }
                    func={
                        event => set_input(event.target.value)
                    }
                />
                <Submit label={ 'Add' } />
            </form>
            <Header text={ 'Numbers' } />
            { people.map(person =>
                <li key={ person.name }>{ person.name }</li>
            )}
        </Fragment>
    )
}

export default App
