import { useState, useEffect } from 'react';

const List = ({ data, keyword, remove }) => {

    // FILTERED RESULTS
    const [filtered, set_filtered] = useState([])

    // FILTER OUT GARBAGE WHEN DATA CHANGES -- FORCE LOWERCASE
    useEffect(() => {
        set_filtered(data.filter(
            person => person.name.toLowerCase().includes(keyword.toLowerCase())
        ))
    }, [data, keyword])

    // CONDITIONAL RENDERING
    switch (filtered.length) {

        // NO PEOPLE FOUND
        case 0: { return (
            <li>No-one matches keyword "{ keyword }"</li>
        )}

        // RENDER PEOPLE
        default: { return (
            filtered.map(person =>
                <div className={ 'splitter' }>
                    <div>{ person.name }</div>
                    <div>{ person.number }</div>
                    <div><button onClick={ () => remove(person.id) }>Remove</button></div>
                </div>
            )
        )}
    }
}

export default List;
