import { useState, Fragment } from 'react';

const App = () => {

    // COMPONENT DATA
    const [local, set_local] = useState({
        good: 0,
        neutral: 0,
        bad: 0
    })

    // INCREMENT CATEGORY
    const increment = (category) => {
        set_local({
            ...local,
            [category]: local[category] + 1
        })
    }

    return (
        <Fragment>
            <Header
                text={ 'Give Feedback' }
            />
            <Button
                label={ 'Good' }
                func={ () => increment('good') }
            />
            <Stats
                data={[
                    ['Good', local.good],
                    ['Neutral', local.neutral],
                    ['Bad', local.bad]
                ]}
            />
        </Fragment>
    )
}

const Header = ({ text }) => { return (
    <h1>{ text }</h1>
)}

const Buttons = ({ keys, increment }) => { return (
    <>
        <Button
            label={ 'Good' }
            func={ () => increment('good') }
        />
        <Button
            label={ 'Neutral' }
            func={ () => increment('neutral') }
        />
        <Button
            label={ 'Bad' }
            func={ () => increment('bad') }
        />
    </>
)}

const Button = ({ label, func }) => { return (
    <button onClick={ func }>{ label }</button>
)}

const Stats = ({ data }) => { return (
    data.map((row, index) =>
        <div key={ index }>{ row[0] }: { row[1] }</div>
    )
)}

export default App
