import { useState, Fragment } from 'react';

const App = () => {

    // COMPONENT DATA
    const [local, set_local] = useState({
        good: 0,
        neutral: 0,
        bad: 0
    })

    // INCREMENT CATEGORY
    const increment = (category) => {
        set_local({
            ...local,
            [category]: local[category] + 1
        })
    }

    return (
        <Fragment>
            <Header
                text={ 'Give Feedback' }
            />
            <Buttons
                keys={ Object.keys(local) }
                increment={ increment }
            />
            <Header
                text={ 'Statistics' }
            />
            <Stats
                keys={ Object.keys(local) }
                values={ local }
            />
        </Fragment>
    )
}

const Header = ({ text }) => { return (
    <h1>{ text }</h1>
)}

const Buttons = ({ keys, increment }) => { return (
    keys.map((name, index) =>
        <button onClick={ () => increment(name) }>{ name }</button>
    )
)}

const Stats = ({ keys, values }) => { return (
    keys.map((name, index) =>
        <div key={ index }>{ name }: { values[name] }</div>
    )
)}

export default App
