import { Fragment, useState } from 'react'
import Header from './Header';
import Field from './Field';
import Submit from './Submit';

const App = () => {

    // LIST OF PEOPLE
    const [people, set_people] = useState([
        { name: 'Arto Hellas' }
    ]) 

    // INPUT STATES
    const [input, set_input] = useState({
        name: '',
        number: 0
    })

    const update_field = (event, category) => {
        set_input({
            ...input,
            [category]: event.target.value
        })
    }

    // ADD PERSON
    const add_person = event => {
        event.preventDefault();

        // IF INPUT IS NOT EMPTY
        if (input.length > 0) {

            // PUSH NEW PERSON
            people.push({
                name: input
            })

            // UPDATE BOTH STATES
            set_people(people)
            set_input('')
        }
    }

    return (
        <Fragment>
            <Header text={ 'Phonebook' } />
            <Form
                trigger={ add_person }
                update={ update_field }
            />
            <Header text={ 'Numbers' } />
            { people.map(person =>
                <li key={ person.name }>{ person.name }</li>
            )}
        </Fragment>
    )
}

const Form = ({ trigger, update_field }) => { return (
    <form onSubmit={ trigger }>
        <Field
            label={ 'Foo' }
            value={ input }
            func={
                event => update_field(event.target.value)
            }
        />
        <Submit label={ 'Add' } />
    </form>
)}

export default App
