import { useState, useEffect } from 'react'
import { Inputs, Text, Number } from './components/inputs'
import Phonebook from './components/phonebook'
import Notifications from './components/notifications'

import axios from 'axios'
import './interface/general.scss'

const App = () => {

    // LIST OF PEOPLE
    const [people, set_people] = useState([])

    // LIST OF NOTIFICATIONS
    const [notifications] = useState([])

    // INPUT STATES
    const [input, set_input] = useState({
        filter: '',
        name: '',
        number: 0
    })

    // ON LOAD, FETCH ALL PEOPLE
    useEffect(() => {
        axios.get('http://localhost:3001/api/persons').then(result => {
            set_people(result.data)
        }).catch(error => {
            console.log(error)
        })
    }, [])

    // UPDATE INPUT STATES
    const update_field = (category, value) => {
        set_input({
            ...input,
            [category]: value
        })
    }

    // const create_user = () => {
    //     set_notifications([
    //         ...notifications, {
    //             type: 'positive',
    //             message: 'foobar',
    //             id: new Date()
    //         }
    //     ])
    // }

    return (
        <div className={ 'container' }>
            <div>
                <Inputs header={ 'Filter Results' }>
                    <Text
                        label={ 'Filter people by name' }
                        value={ input.filter }
                        func={
                            event => update_field('filter', event.target.value)
                        }
                    />
                </Inputs>
                <Phonebook
                    header={ 'phonebook' }
                    data={ people }
                    keyword={ input.filter }
                />
            </div>
            <div>
                <Inputs header={ 'add person' }>
                    <Text
                        label={ 'What is the persons name?' }
                        value={ input.name }
                        func={
                            event => update_field('name', event.target.value)
                        }
                    />
                    <Number
                        label={ 'What is the persons\'s number?' }
                        value={ input.number }
                        func={
                            event => update_field('number', event.target.value)
                        }
                    />
                </Inputs>
                <Notifications
                    header={ 'notifications' }
                    data={ notifications }
                />
            </div>
        </div>
    )
}

export default App;
