import { Fragment, useState } from 'react'

const App = () => {

    // LIST OF PEOPLE
    const [people, set_people] = useState([
        { name: 'Arto Hellas' }
    ]) 

    // INPUT STATE
    const [input, set_input] = useState('')

    return (
        <Fragment>
            <h2>Phonebook</h2>
            <form>
                <Field
                    label={ 'Foo' }
                    value={ input }
                    func={
                        event => set_input(event.target.value)
                    }
                />
                <Submit label={ 'Add' } />
            </form>
            <h2>Numbers</h2>
            ...
        </Fragment>
    )
}

const Field = ({ label, value, func }) => { return (
    <div>
        <input
            placeholder={ label }
            value={ value }
            onChange={ func }
        />
    </div>
)}

const Submit = ({ label }) => { return (
    <div>
        <button>{ label }</button>
    </div>
)}

export default App
