import { remove_person } from '../funcs/api'

const List = ({ data, keyword }) => {

    // FILTER OUT GARBAGE -- FORCE LOWERCASE
    const filtered = data.filter(
        person => person.name.toLowerCase().includes(keyword.toLowerCase())
    )

    const remove = (id) => {
        remove_person('http://localhost:3001/persons', id).then(response => {
            console.log(response)
        })
    }

    switch (filtered.length) {

        // NO PEOPLE FOUND
        case 0: { return (
            <li>No-one matches keyword "{ keyword }"</li>
        )}

        // RENDER PEOPLE
        default: { return (
            filtered.map(person =>
                <li key={ person.id }>
                    { person.name }: { person.number }
                    <button onClick={ () => remove(person.id) }>Remove</button>
                </li>
            )
        )}
    }
}

export default List;
